import { View, ScrollView, Text, StyleSheet, Image } from "react-native";
import React from "react";
import { TouchableOpacity } from "react-native-gesture-handler";
import { MaterialIcons } from "@expo/vector-icons";
import { Divider } from "react-native-paper";

const SettingsScreen = ({ navigation }) => {
  return (
    <ScrollView style={styles.outerContainer}>
      <View style={styles.innerContainer}>
        <TouchableOpacity
          style={{ marginTop: 20 }}
          onPress={() => navigation.push("Edit profile")}
        >
          <View style={styles.settingBlock}>
            <Text style={styles.textStyle}>Edit profile</Text>
            <MaterialIcons
              name="keyboard-arrow-right"
              size={24}
              color="black"
            />
          </View>
          <Divider bold={true} style={{ marginHorizontal: 10 }} />
        </TouchableOpacity>

        <TouchableOpacity
          style={{ marginTop: 20 }}
          onPress={() => navigation.push("Reset password")}
        >
          <View style={styles.settingBlock}>
            <Text style={styles.textStyle}>Reset password</Text>
            <MaterialIcons
              name="keyboard-arrow-right"
              size={24}
              color="black"
            />
          </View>
          <Divider bold={true} style={{ marginHorizontal: 10 }} />
        </TouchableOpacity>

        <TouchableOpacity
          style={{ marginTop: 20 }}
          onPress={() => navigation.push("Delete account")}
        >
          <View style={styles.settingBlock}>
            <Text style={[styles.textStyle, { color: "red" }]}>
              Delete account
            </Text>
            <MaterialIcons name="keyboard-arrow-right" size={24} color="red" />
          </View>

          <Divider
            bold={true}
            style={{ marginHorizontal: 10, marginBottom: 20 }}
          />
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
};

export default SettingsScreen;

const styles = StyleSheet.create({
  outerContainer: {},
  innerContainer: {
    marginHorizontal: 10,
    backgroundColor: "white",
    borderBottomLeftRadius: 10,
    borderBottomRightRadius: 10,
  },
  settingBlock: {
    marginHorizontal: 20,
    marginVertical: 5,
    flexDirection: "row",
    justifyContent: "space-between",
  },
  textStyle: {
    fontSize: 18,
    // fontWeight: "bold",
  },
});
