import {
  ScrollView,
  StyleSheet,
  View,
  Text,
  SafeAreaView,
  RefreshControl,
  Dimensions,
  TouchableOpacity
} from "react-native";
import React, { useEffect, useState } from "react";
import Header from "./Header.js";
import Post4home from "./Post4home.js";
import {
  collection,
  onSnapshot,
  orderBy,
  limit,
  query,
} from "firebase/firestore";
import { db, auth } from "../Firebase";
import Highlights from "./Highlights.js";
import Tools4Home from "./Tools4Home.js";
import NewsPostScreen from "./NewsPostScreen.js";
import Video4home from "./Video4home.js";
import BannerHome_linkedtoApp from "../components/cards/BannerHome_linkedtoApp.js";
import BannerHome_linkedtoWeb from "../components/cards/BannerHome_linkedtoWeb.js";


const wait = (timeout) => {
  return new Promise((resolve) => setTimeout(resolve, timeout));
};
const { width } = Dimensions.get("window");

const HomeScreen = ({ isLoading, navigation }) => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newsposts, setNewsPosts] = useState([]);
  const [highlights, setHighlights] = useState([]);
  const [refreshing, setRefreshing] = useState(false);
  const [videos, setVideos] = useState([]);
  const user = auth.currentUser;

  const onRefresh = React.useCallback(() => {
    setRefreshing(true);
    wait(2000).then(() => setRefreshing(false));
  }, []);

  useEffect(() => {
    const unsub = onSnapshot(
      query(collection(db, "highlights")),
      (snapshot) => {
        setHighlights(
          snapshot.docs.map((highlights) => ({
            id: highlights.id,
            ...highlights.data(),
          }))
        );
        if (loading) {
          setLoading(false);
        }
      }
    );
    return unsub;
  }, []);

  useEffect(() => {
    const unsub = onSnapshot(
      query(collection(db, "posts"), orderBy("created", "desc"), limit(6)),
      (snapshot) => {
        setPosts(
          snapshot.docs.map((post) => ({ id: post.id, ...post.data() }))
        );
        if (loading) {
          setLoading(false);
        }
      }
    );
    return unsub;
  }, []);

  useEffect(() => {
    const unsub = onSnapshot(
      query(collection(db, "videos"), limit(4)),
      (snapshot) => {
        setVideos(
          snapshot.docs.map((video) => ({
            id: video.id,
            ...video.data(),
          }))
        );
        if (loading) {
          setLoading(false);
        }
      }
    );
    return unsub;
  }, []);

  return (
    <SafeAreaView style={styles.mainContainer}>
      <Header navigation={navigation} />
      <View style={{ backgroundColor: 'lightgrey'}}>
      {/* Banners */}
      <BannerHome_linkedtoApp navigation={navigation}/>
      <BannerHome_linkedtoWeb navigation={navigation}/>

      <ScrollView
        style={styles.outerContainer}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
      >
        {/* Highlights section */}
        <View style={styles.highlightsContainer}>
          <Text style={styles.headingText}>Highlights</Text>
          <ScrollView horizontal={true}>
            {highlights.map((highlights, index) => (
              <Highlights
                highlights={highlights}
                key={index}
                navigation={navigation}
              />
            ))}
          </ScrollView>
        </View>

        {/* Post section */}
        <View style={styles.postContainer}>
          <Text style={styles.headingText}>Latest posts</Text>
          <ScrollView horizontal={true}>
            {posts.map((post, index) => (
              <Post4home post={post} key={index} navigation={navigation} />
            ))}
          </ScrollView>
          <TouchableOpacity onPress={() => navigation.push("Posts")}>
            <Text style={styles.seeMoreText}>More posts...</Text>
          </TouchableOpacity>
        </View>

        {/* video section */}
        {/* <Text style={styles.headingText}>Videos</Text> */}
        <ScrollView
          horizontal={true}
          pagingEnabled={true}
          // ref={ScrollView}
          snapToInterval={width - 40}
          // snapToAlignment={"center"}
        >
          {videos.map((video, index) => (
            <Video4home video={video} key={index} navigation={navigation} />
          ))}
        </ScrollView>

        {/* Tools section */}
        <View style={styles.toolsContainer}>
          <Text style={styles.headingText}>Tools</Text>
          {/* List tools here */}
          <Tools4Home navigation={navigation} />

          <TouchableOpacity onPress={() => navigation.push("Tools")}>
            <Text style={styles.seeMoreText}>More tools...</Text>
          </TouchableOpacity>
        </View>

        {/* News section */}
        <View
          style={{
            backgroundColor: "white",
            marginBottom: -5,
            marginTop: 5,
            padding: 5,
          }}
        >
          <Text style={styles.headingText}>Latest News</Text>
        </View>

        <View>
          <NewsPostScreen />
          {/* {newsposts.map((newspost, index) => (
            <NewsPost newspost={newspost} key={index} navigation={navigation} />
          ))} */}
        </View>
        {/* <Divider style={{height: 5}}/> */}
      </ScrollView>
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  mainContainer: {
    backgroundColor: "#1267E9",
    flex: 1
  },
  outerContainer: {
    backgroundColor: "lightgrey",
  },
  highlightsContainer: {
    backgroundColor: "white",
    borderBottomLeftRadius: 10,
    borderBottomRightRadius: 10,
    marginBottom: 5,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },
  postContainer: {
    borderRadius: 10,
    // marginHorizontal: 6,
    marginVertical: 10,
    backgroundColor: "white",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },
  toolsContainer: {
    borderRadius: 10,
    marginHorizontal: 6,
    marginVertical: 10,
    backgroundColor: "white",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },
  headingText: {
    margin: 10,
    fontWeight: "bold",
    fontSize: 20,
    fontWeight: "bold",
    fontSize: 20,
    margin: 10,
  },
  seeMoreText: {
    textAlign: "center",
    color: "#1267E9",
    marginVertical: 20,
    fontSize: 15,
    fontWeight: "bold",
  },
});
export default HomeScreen;
